mapper.select:
select p.product_id,p.product_name,p.product_price,p.product_category,p.product_size,p.product_color, p.product_inventory,p.product_comment,i.file_name,i.upload_path,i.uuid ,i.file_name2,i.upload_path2,i.uuid2 from product p join (select product_id ,max(case when image_id=1 then file_name end) as file_name ,max(case when image_id=1 then upload_path end) as upload_path ,max(case when image_id=1 then uuid end) as uuid ,max(case when image_id=2 then file_name end) as file_name2 ,max(case when image_id=2 then upload_path end) as upload_path2 ,max(case when image_id=2 then uuid end) as uuid2 from product_img group by product_id) i on p.product_id=i.product_id
where product_name like concat('%' , #{keyword}, '%')
order by product_id desc limit #{skip}, #{amount}

mapper.delete:
delete from product_img where product_id=${productId}
and
image_id=${imageId}

mapper.update:
update product set product_name=#{productName},
product_price=#{productPrice},product_category=#{productCategory},product_size=#{productSize},product_color=#{productColor},
product_inventory=#{productInventory},product_comment=#{productComment}
where product_id=#{productId};
